    private void btnRemoveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemoveActionPerformed
        Pallets p = new Pallets();
        while(!pilha.isEmpty()){
        p = pilha.peek();
        if(p.getProduto().equals(txtProduto.getText())){
        p = pilha.pop();
        
        if(Integer.parseInt(txtQtd.getText()) >= p.getQnt()){
            pilha.pop();
            JOptionPane.showMessageDialog(null, "Objeto removido com sucesso'!");    
        } 
        else{
            p = pilha.pop();
            p.setQnt(p.getQnt() - Integer.parseInt(txtQtd.getText()));
            pilha.push(p);                             
            JOptionPane.showMessageDialog(null, "Objeto removido");
            mostra();
        }                                                                                                                                                                                                                                                                                                            
            break;
        }
        else{
          JOptionPane.showMessageDialog(null, "Movendo objeto para auxiliar!"); 
            auxiliar.push(pilha.pop());
            mostra();
        }
        }
        
        while(!auxiliar.isEmpty()){
            pilha.push(auxiliar.pop());
        }
        
    }//GEN-LAST:event_btnRemoveActionPerformed

