    void mostra(){
        listPilha.setText("");
        if(!pilha.isEmpty())
            for(Pallets p: pilha)
                listPilha.append(p.toString()+"\n");
        
       
        listAux.setText("");
      if(!auxiliar.isEmpty())
        for(Pallets p: auxiliar)
        listAux.append(p.toString()+"\n");
        
        if(pilha.isEmpty())
            lblTopo.setText("Topo: Vazio");
        else
        lblTopo.setText(" Topo: " +pilha.peek());
    }
    
    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        Pallets p = new Pallets();
        p.setProduto(txtProduto.getText());
        p.setQnt( Integer.parseInt(txtQtd.getText()));
        pilha.push(p);
        System.out.println(pilha);
        System.out.println(" Topo: "+pilha.peek());
        mostra();
    }//GEN-LAST:event_btnAddActionPerformed

